import { useEffect, useState } from "react";
import { Card, Button} from "react-bootstrap";
import { useNavigate } from "react-router-dom";

import { createNewCard } from "./service/apiCard";
import { getCard } from "./service/apiCard";

function CardListPage() {
  const [users, setUsers] = useState([]);
  const [cards, setCards] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    let userData = JSON.parse(localStorage.getItem("UserData"));
    setUsers(userData);
  }, []);

  const fetchCards = async () => {
    try {
      const response = await getCard();
      console.log(response.data);
      setCards(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    fetchCards();
  }, []);

  return (
    <div className="container">
      <button
        className="btn btn-primary mb-3 mt-3 m-5"
        onClick={() => navigate("/AddCard", { createNewCard })}
      >
        Add Card
      </button>

      <div className="row">
        {cards.map((card, index) => (
          <div key={index} className="col-sm-4 col-md-4">
            <Card border="primary" style={{ width: "25rem" }}>
            <Card.Header>Business card</Card.Header>
              <Card.Body>
                <Card.Title>{card.title}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">
                  {card.subtitle}
                </Card.Subtitle>

                <Card.Text>{card.description}</Card.Text>
                <Card.Text>{card.phone}</Card.Text>
                <Card.Text>{card.email}</Card.Text>
                <Card.Img variant="top" src={"https://picsum.photos/id/10/200/300"} />

                <Button variant="primary">updade card</Button>
                <Button variant="danger">delete card</Button>
              </Card.Body>
            </Card>
          </div>
        ))}
      </div>
    </div>
  );
}
export default CardListPage;
