import { useEffect, useState } from "react";
import { Card, Button, Row, Col} from "react-bootstrap";
import { useNavigate } from "react-router-dom";

import { createNewCard, updateCard, deleteCard } from "./service/apiCard";
import { getCard } from "./service/apiCard";

function CardListPage() {
  const [users, setUsers] = useState([]);
  const [cards, setCards] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    let userData = JSON.parse(localStorage.getItem("UserData"));
    setUsers(userData);
  }, []);

  const fetchCards = async () => {
    console.log("Image URL:", cards.url);
    try {
      const response = await getCard();
      console.log(response.data);
      setCards(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    fetchCards();
  }, []);

  return (
    <div className="container">
      <button
        className="btn btn-primary mb-3 mt-3 m-5"
        onClick={() => navigate("/AddCard", { createNewCard })}
      >
        Add Card
      </button>

      <div className="row">
        {cards.map((card, index) => (
          <div key={index} className="col-sm-3 col-md-3">
            <Card border="primary" style={{ width: "20rem" }}>
            <Card.Header>Business card</Card.Header>
              <Card.Body>
                <Card.Title>{card.title}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">
                  {card.subtitle}
                </Card.Subtitle>

                <Card.Text>{card.description}</Card.Text>
                <Card.Text>{card.phone}</Card.Text>
                <Card.Text>{card.email}</Card.Text>
                <Card.Img variant="top" src={card.url}  style={{ marginBottom: '10px' }} />
                <Row>
                 <Col>
                 <Button variant="danger" size="sm" onClick={() => navigate("/DeleteCard", { deleteCard })}>delete card</Button>
                 </Col>
                 <Col>
                  <Button variant="primary" size="sm" onClick={() => navigate(`/UpdateCard/${card._id}`, { updateCard })}>update card</Button>
                 </Col>
                </Row>
              </Card.Body>
            </Card>
          </div>
        ))}
      </div>
    </div>
  );
}
export default CardListPage;
