import axios from "axios";
import { BaseAPI } from "../constants";

const api = axios.create({
  baseURL: BaseAPI,
});
export const getCard = async () => {
  try {
    const response = await api.get(`/cards/my-cards`, {
      headers: {
        "x-auth-token": localStorage.getItem("token"),
      },
    });
    return response;
  } catch (error) {
    console.error("Error posting item:", error);
    throw error;
  }
};
export const updateCard = async () => {
  try {
    const response = await api.put(
      `/cards/65424d35cb6bcb58697bab4a`,
      {
        title: "a wonderful new card updated!",
        subtitle: "a test value for this card",
        description: "a test value for new card\na test value for new card\n",
        phone: "012-3211234",
        email: "qwe@gmail.com",
        web: "www.bing.com",
        image: {
          url: "https://img.izismile.com/img/img13/20201030/640/you_have_never_seen_something_like_this_640_36.jpg",
          alt: "image of something",
        },
        address: {
          state: "IL",
          country: "Israel",
          city: "Arad",
          street: "Shoham",
          houseNumber: 5,
          zip: 8920435,
        },
      },
      {
        headers: {
          "x-auth-token": localStorage.getItem("token"),
        },
      }
    );
    return response.data;
  } catch (error) {
    console.error("Error updating item:", error);
    throw error;
  }
};

export const deleteCard = async () => {
  try {
    const response = await api.delete(`/65424d35cb6bcb58697bab4a`, {
      headers: {
        "x-auth-token": localStorage.getItem("token"),
      },
    });
    return response.data;
  } catch (error) {
    console.error("Error deleting item:", error);
    throw error;
  }
};

export const createNewCard = async (token, card) => {
  console.log("text", card);
  try {
    const response = await api.post(
      `/cards`,
      {
        title: card.title,
        subtitle: card.subtitle,
        description: card.description,
        phone: card.phone,
        email: card.email,
        web: card.web,
        image: {
          url: card.url,
          alt: card.alt,
        },
        address: {
          state: card.state,
          country: card.country,
          city: card.city,
          street: card.street,
          houseNumber: +card.houseNumber,
          zip: +card.zip,
        },
      },
      {
        headers: {
          "x-auth-token": token,
        },
      }
    );
    return response;
  } catch (error) {
    console.error("Error posting item:", error);
    throw error;
  }
};
