import { useContext } from "react";
import { ThemeContext } from "../Context/ThemeContext";
import { Link, useNavigate } from "react-router-dom";
import { Navbar, Container, Nav, NavDropdown } from "react-bootstrap";

function NavigationBar (){

    const isLoggedIn = localStorage.getItem("isLoggedIn");
    const userName = isLoggedIn ? localStorage.getItem("userName") : "N/A";
    const nav = useNavigate();
    const  { theme, toggleTheme }  = useContext(ThemeContext);
    const  textColor = theme === "dark" ? "text-light" : "text-dark";

    const handleLogout =()=>{
        localStorage.removeItem("isLoggedIn");
        localStorage.removeItem("userName");
        nav("/");
    };
    return(
        <Navbar expand="lg" className={`navbar navbar-${theme} bg-${theme}`}>
      <Container>
        <Navbar.Brand className="navbar-brand">
                      <img
                      src="https://png.pngtree.com/png-clipart/20200720/original/pngtree-floral-golden-ornamental-letter-z-png-image_4773367.jpg" className="me-4" height="60"
                      alt="Site logo" loading="lazy" />
                      <strong>Ziv's app</strong>
            </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">

            <Nav.Link to="/AboutMe" className="btn btn-light navbar-light">
              About
            </Nav.Link>
            <Nav.Link to="/UserListPage" className="btn btn-light navbar-light">
              My Cards
            </Nav.Link> 
            <div className="input-group-text">
                <input className="form-control" type="text"  placeholder="search"/>
                <button className="btn btn-outline-secondary bg-whitel ms-n3" type="button">
                        <i className="bi bi-search"></i>
                </button>
            </div>
            <Nav.Link to="/RegisterForm" className="btn btn-light navbar-light">
                  signup
            </Nav.Link>
            <Nav.Link to="/" className="nav-link">
                    Login
            </Nav.Link>
              <div className="form-check form-switch">
                  <input type="checkbox"
                         id="cdtoggleTheme"
                         className="form-check-input"
                         onChange={toggleTheme}
                         value={theme === "dark"}
                      />
                      <label
                      className={`form-check-label ${textColor} text-capitalize`} htmlFor="cdToggleTheme">
                      {theme}
                      <i className={`bi bi-${
                       theme === "dark" ? "moon-fill"  :  "brightness-high-fill"} ms-2`}>
                        </i>
                      </label>
              </div>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
    );
}
export default NavigationBar;
